        protected void Page_Load(object sender, EventArgs e)
        {
            try
            {
                if (Request.QueryString["Id_Usuario"] != null)
                {
                    if (Response.Cookies.Count > 0 && Session["cerrar"] != null)
                    {
                        if (!IsPostBack)
                        {
                            cargar_tabla_roles();
                            cargar_drop();
                        }
                        else
                        {
                            cargar_tabla_roles();
                        }
                    }
                    else
                    {
                        Page.Response.Redirect("~/Login", false);
                    }
                }
                else
                {
                    Page.Response.Redirect("~/Login", true);
                }
            }
            catch (Exception)
            {
                Page.Response.Redirect("~/Login", true);
            }

        }

        protected void cargar_drop()
        {
            try
            {
                DataTable dt_;
                DCL.Int_Usuarios obj_ = new DCL.Int_Usuarios();
                dt_ = Int_Usuarios_BRL.SelectTable(obj_, 6);
                drop_rol.DataSource = dt_;
                drop_rol.DataTextField = "Nombre_Rol";
                drop_rol.DataValueField = "Id_Rol";
                drop_rol.DataBind();
                drop_rol.Items.Insert(0, new ListItem("-Seleccione roles-", ""));

                drop_rol_usu.DataSource = dt_;
                drop_rol_usu.DataTextField = "Nombre_Rol";
                drop_rol_usu.DataValueField = "Id_Rol";
                drop_rol_usu.DataBind();
            }
            catch (Exception ex)
            {
                Console.WriteLine(ex);
            }
        }

        #region
        protected void crear_nuevo_usu(object sender, EventArgs e)
        {
            try
            {
                if (
                    !String.IsNullOrEmpty(txt_usuario.Text.Trim()) &&
                    !String.IsNullOrEmpty(txt_contraseña.Text.Trim()) &&
                    drop_rol.SelectedValue != string.Empty
                    )
                {
                    string parametro = Request.QueryString["Id_Usuario"].ToString();
                    string encrytar = GetMD5(txt_contraseña.Text.Trim());

                    DCL.Int_Usuarios obj = new DCL.Int_Usuarios();
                    obj.Usuario = txt_usuario.Text.Trim();
                    obj.Contraseña = encrytar;
                    obj.Id_Rol = Convert.ToInt32(drop_rol.SelectedValue);
                    obj.Usuario_Creacion = parametro;
                    Int_Usuarios_BRL.InsertOrUpdate(obj, 28);

                    Page.Response.Redirect(Page.Request.Url.ToString(), true);
                }
                else
                {

                }
            }
            catch (Exception ex)
            {
                Console.WriteLine(ex);
            }
        }
