        protected void restablecer_contraseña(object sender, EventArgs e)
        {
            try
            {
                string Id_Usuario = Request.Form["rd_estado_usu"].ToString();

                DataTable dt_;
                DCL.Int_Usuarios obj_ = new DCL.Int_Usuarios();
                obj_.Id_Usuario = Convert.ToInt32(Id_Usuario);
                dt_ = Int_Usuarios_BRL.SelectTable(obj_, 29);
                if (dt_.Rows.Count > 0)
                {
                    string encrytar = GetMD5(dt_.Rows[0]["Usuario"].ToString());
                    DCL.Int_Usuarios obj = new DCL.Int_Usuarios();
                    obj.Contraseña = encrytar;
                    obj.Id_Usuario = Convert.ToInt32(Id_Usuario);
                    Int_Usuarios_BRL.InsertOrUpdate(obj, 31);

                    Page.Response.Redirect(Page.Request.Url.ToString(), true);
                }
            }
            catch (Exception ex)
            {
                Console.WriteLine(ex);
            }
        }
